import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = ({ role }) => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    // Check role from localStorage to persist session
    const storedRole = localStorage.getItem("role");
    if (!storedRole) {
      navigate("/"); // Redirect to login if no role is found
    }
  }, [navigate]);

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        {
          id: Date.now(),
          title: newRequest,
          status: "Pending",
          date: new Date().toISOString().split("T")[0],
          screenshot,
        },
      ]);
      setNewRequest("");
      setScreenshot(null);
    }
  };

  const handleResolveRequest = (id) => {
    const description = prompt("Please enter a resolution description:");
    if (description) {
      setRequests(
        requests.map((req) =>
          req.id === id
            ? { ...req, status: "Resolved", date: new Date().toISOString().split("T")[0], description }
            : req
        )
      );
    }
  };

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>

            {/* Show request form only for users */}
            {role === "user" && (
              <div className="filter-section">
                <input type="text" placeholder="Enter a new request" value={newRequest} onChange={(e) => setNewRequest(e.target.value)} />
                <button onClick={handleAddRequest} className="primary-button">Add Request</button>
              </div>
            )}

            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {requests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>
                        {role === "admin" && req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req.id)} className="resolve-button">
                            Resolve
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
