import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending" },
    { id: 2, title: "Printer Issue", status: "Resolved" },
    { id: 3, title: "Network Downtime", status: "In Progress" },
    { id: 4, title: "Software Installation", status: "Pending" },
  ]);
  const [newRequest, setNewRequest] = useState("");
  const [settings, setSettings] = useState({ emailNotifications: true, theme: "Light" });

  // Handle navigation
  const handleSidebarClick = (section) => {
    setActiveSection(section);
  };

  // Function to log out
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  // Handle adding a new request
  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([...requests, { id: Date.now(), title: newRequest, status: "Pending" }]);
      setNewRequest("");
    }
  };

  // Handle changing the status of a pending request
  const handleChangeStatus = (id, newStatus) => {
    const updatedRequests = requests.map((req) =>
      req.id === id ? { ...req, status: newStatus } : req
    );
    setRequests(updatedRequests);
  };

  // Handle updating settings
  const handleToggleNotifications = () => {
    setSettings({ ...settings, emailNotifications: !settings.emailNotifications });
  };

  const handleThemeChange = (event) => {
    setSettings({ ...settings, theme: event.target.value });
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li
            className={activeSection === "Dashboard" ? "active" : ""}
            onClick={() => handleSidebarClick("Dashboard")}
          >
            Dashboard
          </li>
          <li
            className={activeSection === "Requests" ? "active" : ""}
            onClick={() => handleSidebarClick("Requests")}
          >
            Requests
          </li>
          <li
            className={activeSection === "Reports" ? "active" : ""}
            onClick={() => handleSidebarClick("Reports")}
          >
            Reports
          </li>
          <li
            className={activeSection === "Settings" ? "active" : ""}
            onClick={() => handleSidebarClick("Settings")}
          >
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <h2>Pending Requests</h2>
            <div className="requests-grid">
              {requests
                .filter((req) => req.status === "Pending")
                .map((req) => (
                  <div key={req.id} className="request-card">
                    <h3>{req.title}</h3>
                    <p>
                      Status:{" "}
                      <span className="status pending">{req.status}</span>
                    </p>
                    <button onClick={() => handleChangeStatus(req.id, "Resolved")}>
                      Mark as Resolved
                    </button>
                    <button onClick={() => handleChangeStatus(req.id, "In Progress")}>
                      Mark as In Progress
                    </button>
                  </div>
                ))}
            </div>
          </>
        )}
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <input
              type="text"
              placeholder="Enter a new request"
              value={newRequest}
              onChange={(e) => setNewRequest(e.target.value)}
            />
            <button onClick={handleAddRequest}>Add Request</button>
            <h2>All Requests</h2>
            <div className="requests-grid">
              {requests.map((req) => (
                <div key={req.id} className="request-card">
                  <h3>{req.title}</h3>
                  <p>Status: <span className={`status ${req.status.toLowerCase()}`}>{req.status}</span></p>
                </div>
              ))}
            </div>
          </>
        )}
        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <h2>Resolved Requests</h2>
            <div className="requests-grid">
              {requests
                .filter((req) => req.status === "Resolved")
                .map((req) => (
                  <div key={req.id} className="request-card">
                    <h3>{req.title}</h3>
                    <p>
                      Status: <span className="status resolved">{req.status}</span>
                    </p>
                  </div>
                ))}
            </div>
          </>
        )}
        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <div>
              <label>
                <input
                  type="checkbox"
                  checked={settings.emailNotifications}
                  onChange={handleToggleNotifications}
                />
                Enable Email Notifications
              </label>
            </div>
            <div>
              <label>
                Theme:
                <select value={settings.theme} onChange={handleThemeChange}>
                  <option value="Light">Light</option>
                  <option value="Dark">Dark</option>
                </select>
              </label>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
