import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending", date: "2023-12-01", screenshot: null },
    { id: 2, title: "Printer Issue", status: "Resolved", date: "2023-12-02", screenshot: null, description: "Replaced faulty hardware." },
    { id: 3, title: "Network Downtime", status: "In Progress", date: "2023-12-03", screenshot: null },
    { id: 4, title: "Software Installation", status: "Pending", date: "2023-12-04", screenshot: null },
  ]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");
  const [resolveModal, setResolveModal] = useState({ isOpen: false, requestId: null });
  const [resolveDescription, setResolveDescription] = useState("");

  const handleSidebarClick = (section) => setActiveSection(section);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        {
          id: Date.now(),
          title: newRequest,
          status: "Pending",
          date: new Date().toISOString().split("T")[0],
          screenshot,
        },
      ]);
      setNewRequest("");
      setScreenshot(null);
    }
  };

  const handleResolveRequest = (id) => {
    setResolveModal({ isOpen: true, requestId: id });
  };

  const handleResolveSubmit = () => {
    if (resolveDescription.trim() !== "") {
      setRequests(
        requests.map((req) =>
          req.id === resolveModal.requestId
            ? { ...req, status: "Resolved", date: new Date().toISOString().split("T")[0], description: resolveDescription }
            : req
        )
      );
      setResolveModal({ isOpen: false, requestId: null });
      setResolveDescription("");
    }
  };

  const handleResolveCancel = () => {
    setResolveModal({ isOpen: false, requestId: null });
    setResolveDescription("");
  };

  const handleFilterChange = (status) => setFilter(status);

  const handleScreenshotUpload = (event) => {
    const file = event.target.files[0];
    if (file && (file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg")) {
      const reader = new FileReader();
      reader.onload = (e) => setScreenshot(e.target.result);
      reader.readAsDataURL(file);
    } else {
      alert("Please upload a valid image (JPG, JPEG, PNG)");
    }
  };

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <p>Welcome, {userProfile.name}!</p>
            <p>Email: {userProfile.email}</p>
          </>
        )}
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input
                type="text"
                placeholder="Enter a new request"
                value={newRequest}
                onChange={(e) => setNewRequest(e.target.value)}
                className="input-field"
              />
              <input
                type="file"
                accept=".jpg,.jpeg,.png"
                onChange={handleScreenshotUpload}
                className="file-upload"
              />
              <button onClick={handleAddRequest} className="primary-button">
                Add Request
              </button>
              <select onChange={(e) => handleFilterChange(e.target.value)} className="filter-dropdown">
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Screenshot</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>
                        {req.screenshot && (
                          <img src={req.screenshot} alt="Issue Screenshot" className="table-screenshot" />
                        )}
                      </td>
                      <td>
                        {req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req.id)} className="resolve-button">
                            Resolve
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <div className="timeline">
              {requests
                .filter((req) => req.status === "Resolved")
                .map((req) => (
                  <div key={req.id} className="timeline-item">
                    <h3>{req.title}</h3>
                    <p>Resolved on: {req.date}</p>
                    <p>Description: {req.description}</p>
                  </div>
                ))}
            </div>
          </>
        )}
        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <p>Adjust your preferences and manage your profile here.</p>
          </>
        )}
      </div>

      {resolveModal.isOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Resolve Request</h2>
            <textarea
              placeholder="Enter resolution description"
              value={resolveDescription}
              onChange={(e) => setResolveDescription(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleResolveSubmit} className="modal-button confirm">
                Submit
              </button>
              <button onClick={handleResolveCancel} className="modal-button cancel">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
