import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";
import { getRequests, createRequest, resolveRequest } from "../api"; // Import API functions

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([]); // ✅ Fetch requests from DB
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");
  const [requestSuggestions] = useState([
    "System Upgrade",
    "Internet Connectivity",
    "Printer Issue",
    "Software Installation",
    "Network Downtime",
    "Technical Glitch",
  ]);
  const [notifications, setNotifications] = useState(true);

  // ✅ Get user role from localStorage
  const role = localStorage.getItem("role");

  // ✅ Fetch requests from DB on load
  useEffect(() => {
    const fetchRequests = async () => {
      const data = await getRequests();
      setRequests(data);
    };
    fetchRequests();
  }, []);

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/");
  };

  // ✅ Save new requests to DB
  const handleAddRequest = async () => {
    if (newRequest.trim() !== "") {
      const requestData = { title: newRequest, screenshot };
      const savedRequest = await createRequest(requestData);
      setRequests([...requests, savedRequest]); // Add request to UI
      setNewRequest("");
      setScreenshot(null);
    }
  };

  // ✅ Resolve requests (Admin only)
  const handleResolveRequest = async (id) => {
    if (role !== "admin") return;
    const description = prompt("Please enter a resolution description:");
    if (description) {
      await resolveRequest(id, description);
      setRequests(
        requests.map((req) =>
          req._id === id ? { ...req, status: "Resolved", date: new Date().toISOString().split("T")[0], description } : req
        )
      );
    }
  };

  const handleFilterChange = (status) => setFilter(status);
  const handleThemeChange = (newTheme) => setTheme(newTheme);

  const handleScreenshotUpload = (event) => {
    const file = event.target.files[0];
    if (file && (file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg")) {
      const reader = new FileReader();
      reader.onload = (e) => setScreenshot(e.target.result);
      reader.readAsDataURL(file);
    } else {
      alert("Please upload a valid image (JPG, JPEG, PNG)");
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setNewRequest(suggestion);
  };

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <div className="dashboard-analytics">
              <div className="analytics-card total">
                <h3>Total Requests</h3>
                <p>{requests.length}</p>
              </div>
              <div className="analytics-card pending">
                <h3>Pending Requests</h3>
                <p>{requests.filter((req) => req.status === "Pending").length}</p>
              </div>
              <div className="analytics-card resolved">
                <h3>Resolved Requests</h3>
                <p>{requests.filter((req) => req.status === "Resolved").length}</p>
              </div>
            </div>
          </>
        )}
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input type="text" placeholder="Enter a new request" value={newRequest} onChange={(e) => setNewRequest(e.target.value)} className="input-field" />
              <div className="auto-suggestions">
                {newRequest &&
                  requestSuggestions
                    .filter((suggestion) => suggestion.toLowerCase().includes(newRequest.toLowerCase()))
                    .map((suggestion, index) => (
                      <div key={index} onClick={() => handleSuggestionClick(suggestion)} className="suggestion-item">
                        {suggestion}
                      </div>
                    ))}
              </div>
              <input type="file" accept=".jpg,.jpeg,.png" onChange={handleScreenshotUpload} className="file-upload" />
              {screenshot && <img src={screenshot} alt="Uploaded Screenshot" className="screenshot-preview" style={{ width: "150px", height: "auto", borderRadius: "5px" }} />}
              <button onClick={handleAddRequest} className="primary-button">
                Add Request
              </button>
              <select onChange={(e) => handleFilterChange(e.target.value)} className="filter-dropdown">
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Screenshot</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req._id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>{req.screenshot && <img src={req.screenshot} alt="Issue Screenshot" className="table-screenshot" style={{ width: "100px", height: "auto", borderRadius: "5px" }} />}</td>
                      <td>
                        {role === "admin" && req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req._id)} className="resolve-button">
                            Resolve
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
