import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending", date: "2023-12-01", screenshot: null },
    { id: 2, title: "Printer Issue", status: "Resolved", date: "2023-12-02", screenshot: null, description: "Replaced faulty hardware." },
    { id: 3, title: "Network Downtime", status: "In Progress", date: "2023-12-03", screenshot: null },
    { id: 4, title: "Software Installation", status: "Pending", date: "2023-12-04", screenshot: null },
  ]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");
  const [suggestions, setSuggestions] = useState([]);
  const predefinedRequests = ["System Upgrade", "Internet Connectivity", "Technical Glitch", "Printer Issue"];

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        {
          id: Date.now(),
          title: newRequest,
          status: "Pending",
          date: new Date().toISOString().split("T")[0],
          screenshot,
        },
      ]);
      setNewRequest("");
      setScreenshot(null);
      setSuggestions([]);
    }
  };

  const handleInputChange = (value) => {
    setNewRequest(value);
    if (value.trim()) {
      setSuggestions(predefinedRequests.filter((req) => req.toLowerCase().includes(value.toLowerCase())));
    } else {
      setSuggestions([]);
    }
  };

  const handleSelectSuggestion = (suggestion) => {
    setNewRequest(suggestion);
    setSuggestions([]);
  };

  const handleScreenshotUpload = (event) => {
    const file = event.target.files[0];
    if (file && (file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg")) {
      const reader = new FileReader();
      reader.onload = (e) => setScreenshot(e.target.result);
      reader.readAsDataURL(file);
    } else {
      alert("Please upload a valid image (JPG, JPEG, PNG)");
    }
  };

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Enter a new request"
                  value={newRequest}
                  onChange={(e) => handleInputChange(e.target.value)}
                  className="input-field"
                />
                {suggestions.length > 0 && (
                  <div className="auto-suggestions">
                    {suggestions.map((suggestion, index) => (
                      <div
                        key={index}
                        onClick={() => handleSelectSuggestion(suggestion)}
                        className="suggestion-item"
                      >
                        {suggestion}
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <input
                type="file"
                accept=".jpg,.jpeg,.png"
                onChange={handleScreenshotUpload}
                className="file-upload"
              />
              {screenshot && <img src={screenshot} alt="Uploaded Screenshot" className="screenshot-preview" />}
              <button onClick={handleAddRequest} className="primary-button">
                Add Request
              </button>
              <select onChange={(e) => setFilter(e.target.value)} className="filter-dropdown">
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Screenshot</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>
                        {req.screenshot && (
                          <img src={req.screenshot} alt="Issue Screenshot" className="table-screenshot" />
                        )}
                      </td>
                      <td>
                        {req.status === "Pending" && (
                          <button
                            onClick={() => {
                              const description = prompt("Please enter a resolution description:");
                              if (description) {
                                setRequests(
                                  requests.map((item) =>
                                    item.id === req.id
                                      ? { ...item, status: "Resolved", description }
                                      : item
                                  )
                                );
                              }
                            }}
                            className="resolve-button"
                          >
                            Resolve
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
