import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending", date: "2023-12-01", description: "" },
    { id: 2, title: "Printer Issue", status: "Resolved", date: "2023-12-02", description: "Replaced faulty hardware." },
    { id: 3, title: "Network Downtime", status: "In Progress", date: "2023-12-03", description: "" },
    { id: 4, title: "Software Installation", status: "Pending", date: "2023-12-04", description: "" },
  ]);
  const [newRequest, setNewRequest] = useState("");
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");
  const [userProfile] = useState({
    name: "John Doe",
    email: "john.doe@example.com",
    role: "Administrator",
  });

  const handleSidebarClick = (section) => {
    setActiveSection(section);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        { id: Date.now(), title: newRequest, status: "Pending", date: new Date().toISOString().split("T")[0], description: "" },
      ]);
      setNewRequest("");
    }
  };

  const handleResolveRequest = (id) => {
    const description = prompt("Please enter a resolution description:");
    if (description) {
      const updatedRequests = requests.map((req) =>
        req.id === id ? { ...req, status: "Resolved", date: new Date().toISOString().split("T")[0], description } : req
      );
      setRequests(updatedRequests);
    }
  };

  const handleFilterChange = (status) => {
    setFilter(status);
  };

  const handleThemeChange = (newTheme) => {
    setTheme(newTheme);
  };

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li
            className={activeSection === "Dashboard" ? "active" : ""}
            onClick={() => handleSidebarClick("Dashboard")}
          >
            Dashboard
          </li>
          <li
            className={activeSection === "Requests" ? "active" : ""}
            onClick={() => handleSidebarClick("Requests")}
          >
            Requests
          </li>
          <li
            className={activeSection === "Reports" ? "active" : ""}
            onClick={() => handleSidebarClick("Reports")}
          >
            Reports
          </li>
          <li
            className={activeSection === "Settings" ? "active" : ""}
            onClick={() => handleSidebarClick("Settings")}
          >
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <div className="dashboard-analytics">
              <div className="analytics-card total">
                <h3>Total Requests</h3>
                <p>{requests.length}</p>
              </div>
              <div className="analytics-card pending">
                <h3>Pending Requests</h3>
                <p>{requests.filter((req) => req.status === "Pending").length}</p>
              </div>
              <div className="analytics-card resolved">
                <h3>Resolved Requests</h3>
                <p>{requests.filter((req) => req.status === "Resolved").length}</p>
              </div>
            </div>
          </>
        )}
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input
                type="text"
                placeholder="Enter a new request"
                value={newRequest}
                onChange={(e) => setNewRequest(e.target.value)}
                className="input-field"
              />
              <button onClick={handleAddRequest} className="primary-button">
                Add Request
              </button>
              <select onChange={(e) => handleFilterChange(e.target.value)} className="filter-dropdown">
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>
                        {req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req.id)} className="resolve-button">
                            Resolve
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <div className="timeline">
              {requests
                .filter((req) => req.status === "Resolved")
                .map((req) => (
                  <div key={req.id} className="timeline-item">
                    <h3>{req.title}</h3>
                    <p>Resolved on: {req.date}</p>
                    <p>Description: {req.description}</p>
                  </div>
                ))}
            </div>
          </>
        )}
        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <div className="settings-options">
              <h3>User Profile</h3>
              <p>Name: {userProfile.name}</p>
              <p>Email: {userProfile.email}</p>
              <p>Role: {userProfile.role}</p>
              <h3>Theme</h3>
              <label>
                <input
                  type="radio"
                  name="theme"
                  value="Light"
                  checked={theme === "Light"}
                  onChange={() => handleThemeChange("Light")}
                />
                Light Theme
              </label>
              <label>
                <input
                  type="radio"
                  name="theme"
                  value="Dark"
                  checked={theme === "Dark"}
                  onChange={() => handleThemeChange("Dark")}
                />
                Dark Theme
              </label>
              <button className="reset-button" onClick={() => setTheme("Light")}>
                Reset to Default
              </button>
            </div>
          </>
        )}
