import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const API_BASE_URL = "http://localhost:5000"; // Change this if your backend URL differs

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");

  // Auto-suggestions data
  const requestSuggestions = [
    "System Upgrade",
    "Internet Connectivity",
    "Printer Issue",
    "Software Installation",
    "Network Downtime",
    "Technical Glitch",
  ];

  useEffect(() => {
    fetchRequests();
  }, []);

  // Fetch all requests from the backend
  const fetchRequests = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/requests`);
      setRequests(response.data);
    } catch (error) {
      console.error("Error fetching requests:", error);
    }
  };

  // Add a new request
  const handleAddRequest = async () => {
    if (newRequest.trim() !== "") {
      const newReq = {
        title: newRequest,
        status: "Pending",
        date: new Date().toISOString().split("T")[0],
        screenshot,
      };

      try {
        await axios.post(`${API_BASE_URL}/requests`, newReq);
        fetchRequests();
      } catch (error) {
        console.error("Error adding request:", error);
      }
      setNewRequest("");
      setScreenshot(null);
    }
  };

  // Resolve a request
  const handleResolveRequest = async (id) => {
    const description = prompt("Please enter a resolution description:");
    if (description) {
      try {
        await axios.put(`${API_BASE_URL}/requests/${id}`, {
          status: "Resolved",
          description,
        });
        fetchRequests();
      } catch (error) {
        console.error("Error resolving request:", error);
      }
    }
  };

  // File Upload Handling
  const handleScreenshotUpload = (event) => {
    const file = event.target.files[0];
    if (file && (file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg")) {
      const reader = new FileReader();
      reader.onload = (e) => setScreenshot(e.target.result);
      reader.readAsDataURL(file);
    } else {
      alert("Please upload a valid image (JPG, JPEG, PNG)");
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setNewRequest(suggestion);
  };

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleFilterChange = (status) => setFilter(status);

  const handleThemeChange = (newTheme) => {
    setTheme(newTheme);
    document.body.className = newTheme.toLowerCase() === "dark" ? "dark-theme" : "light-theme";
  };

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <div className="dashboard-analytics">
              <div className="analytics-card total">
                <h3>Total Requests</h3>
                <p>{requests.length}</p>
              </div>
              <div className="analytics-card pending">
                <h3>Pending Requests</h3>
                <p>{requests.filter((req) => req.status === "Pending").length}</p>
              </div>
              <div className="analytics-card resolved">
                <h3>Resolved Requests</h3>
                <p>{requests.filter((req) => req.status === "Resolved").length}</p>
              </div>
            </div>
          </>
        )}

        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input type="text" placeholder="Enter a new request" value={newRequest} onChange={(e) => setNewRequest(e.target.value)} className="input-field" />
              <div className="auto-suggestions">
                {newRequest &&
                  requestSuggestions
                    .filter((suggestion) => suggestion.toLowerCase().includes(newRequest.toLowerCase()))
                    .map((suggestion, index) => (
                      <div key={index} onClick={() => handleSuggestionClick(suggestion)}>
                        {suggestion}
                      </div>
                    ))}
              </div>
              <input type="file" accept=".jpg,.jpeg,.png" onChange={handleScreenshotUpload} className="file-upload" />
              {screenshot && <img src={screenshot} alt="Uploaded Screenshot" className="screenshot-preview" />}
              <button onClick={handleAddRequest} className="primary-button">Add Request</button>
            </div>

            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Screenshot</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>{req.screenshot && <img src={req.screenshot} alt="Issue Screenshot" className="table-screenshot" />}</td>
                      <td>{req.status === "Pending" && <button onClick={() => handleResolveRequest(req.id)} className="resolve-button">Resolve</button>}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <div className="timeline">
              {requests.filter((req) => req.status === "Resolved").map((req) => (
                <div key={req.id} className="timeline-item">
                  <h3>{req.title}</h3>
                  <p>Resolved on: {req.date}</p>
                  <p>Description: {req.description}</p>
                </div>
              ))}
            </div>
          </>
        )}

        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <button onClick={() => handleThemeChange("Light")} className="theme-button">Light Theme</button>
            <button onClick={() => handleThemeChange("Dark")} className="theme-button">Dark Theme</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
