import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending", date: "2023-12-01", screenshot: null },
    { id: 2, title: "Printer Issue", status: "Resolved", date: "2023-12-02", screenshot: null, description: "Replaced faulty hardware." },
    { id: 3, title: "Network Downtime", status: "In Progress", date: "2023-12-03", screenshot: null },
    { id: 4, title: "Software Installation", status: "Pending", date: "2023-12-04", screenshot: null },
  ]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");
  const [resolveModal, setResolveModal] = useState({ isOpen: false, requestId: null });
  const [resolveDescription, setResolveDescription] = useState("");

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        {
          id: Date.now(),
          title: newRequest,
          status: "Pending",
          date: new Date().toISOString().split("T")[0],
          screenshot,
        },
      ]);
      setNewRequest("");
      setScreenshot(null);
    }
  };

  const handleResolveRequest = (id) => {
    setResolveModal({ isOpen: true, requestId: id });
  };

  const handleResolveSubmit = () => {
    if (resolveDescription.trim() !== "") {
      setRequests(
        requests.map((req) =>
          req.id === resolveModal.requestId
            ? {
                ...req,
                status: "Resolved",
                date: new Date().toISOString().split("T")[0],
                description: resolveDescription,
              }
            : req
        )
      );
      setResolveModal({ isOpen: false, requestId: null });
      setResolveDescription("");
    }
  };

  const handleResolveCancel = () => {
    setResolveModal({ isOpen: false, requestId: null });
    setResolveDescription("");
  };

  const handleFilterChange = (status) => setFilter(status);

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <div className="dashboard-analytics">
              <div className="analytics-card total">
                <h3>Total Requests</h3>
                <p>{requests.length}</p>
              </div>
              <div className="analytics-card pending">
                <h3>Pending Requests</h3>
                <p>{requests.filter((req) => req.status === "Pending").length}</p>
              </div>
              <div className="analytics-card resolved">
                <h3>Resolved Requests</h3>
                <p>{requests.filter((req) => req.status === "Resolved").length}</p>
              </div>
            </div>
          </>
        )}
        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input
                type="text"
                placeholder="Enter a new request"
                value={newRequest}
                onChange={(e) => setNewRequest(e.target.value)}
                className="input-field"
              />
              <input
                type="file"
                accept=".jpg,.jpeg,.png"
                onChange={(e) => setScreenshot(e.target.files[0])}
                className="file-upload"
              />
              {screenshot && <img src={URL.createObjectURL(screenshot)} alt="Uploaded Screenshot" />}
              <button onClick={handleAddRequest} className="primary-button">
                Add Request
              </button>
              <select onChange={(e) => handleFilterChange(e.target.value)}>
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Screenshot</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td>{req.status}</td>
                      <td>{req.date}</td>
                      <td>{req.screenshot ? <img src={req.screenshot} alt="Screenshot" /> : "No Screenshot"}</td>
                      <td>
                        {req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req.id)}>Resolve</button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <p>Reports content goes here.</p>
          </>
        )}
        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <p>Settings content goes here.</p>
          </>
        )}
        {resolveModal.isOpen && (
          <div className="modal">
            <h2>Resolve Request</h2>
            <textarea
              placeholder="Enter resolution description"
              value={resolveDescription}
              onChange={(e) => setResolveDescription(e.target.value)}
            ></textarea>
            <button onClick={handleResolveSubmit}>Submit</button>
            <button onClick={handleResolveCancel}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
