import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const Dashboard = () => {
  const navigate = useNavigate();

  // Mock data for service requests
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending" },
    { id: 2, title: "Printer Issue", status: "Resolved" },
    { id: 3, title: "Network Downtime", status: "In Progress" },
    { id: 4, title: "Software Installation", status: "Pending" },
  ]);

  // Function to log out
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h2>Service Portal</h2>
        <ul>
          <li>Dashboard</li>
          <li>Requests</li>
          <li>Reports</li>
          <li>Settings</li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        <h1>Dashboard</h1>
        <div className="summary">
          <div className="summary-card">
            <h3>Total Requests</h3>
            <p>{requests.length}</p>
          </div>
          <div className="summary-card">
            <h3>Pending Requests</h3>
            <p>{requests.filter((req) => req.status === "Pending").length}</p>
          </div>
          <div className="summary-card">
            <h3>Resolved Requests</h3>
            <p>{requests.filter((req) => req.status === "Resolved").length}</p>
          </div>
        </div>
        <h2>Service Requests</h2>
        <div className="requests-grid">
          {requests.map((req) => (
            <div key={req.id} className="request-card">
              <h3>{req.title}</h3>
              <p>
                Status:{" "}
                <span className={`status ${req.status.toLowerCase().replace(" ", "-")}`}>
                  {req.status}
                </span>
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
