import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const API_BASE_URL = "http://localhost:5000"; // Change this if your backend URL differs

const Dashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState("Dashboard");
  const [requests, setRequests] = useState([]);
  const [newRequest, setNewRequest] = useState("");
  const [screenshot, setScreenshot] = useState(null);
  const [theme, setTheme] = useState("Light");
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    fetchRequests();
  }, []);

  // Fetch all requests from the backend
  const fetchRequests = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/requests`);
      setRequests(response.data);
    } catch (error) {
      console.error("Error fetching requests:", error);
    }
  };

  // Fetch only resolved requests for Reports
  const fetchResolvedRequests = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/requests/resolved`);
      return response.data;
    } catch (error) {
      console.error("Error fetching resolved requests:", error);
      return [];
    }
  };

  // Add a new request
  const handleAddRequest = async () => {
    if (newRequest.trim() !== "") {
      const newReq = {
        title: newRequest,
        status: "Pending",
        date: new Date().toISOString().split("T")[0],
        screenshot,
      };

      try {
        await axios.post(`${API_BASE_URL}/requests`, newReq);
        fetchRequests(); // Refresh requests list
      } catch (error) {
        console.error("Error adding request:", error);
      }
      setNewRequest("");
      setScreenshot(null);
    }
  };

  // Resolve a request
  const handleResolveRequest = async (id) => {
    const description = prompt("Please enter a resolution description:");
    if (description) {
      try {
        await axios.put(`${API_BASE_URL}/requests/${id}`, {
          status: "Resolved",
          description,
        });
        fetchRequests(); // Refresh list after resolving
      } catch (error) {
        console.error("Error resolving request:", error);
      }
    }
  };

  const handleSidebarClick = (section) => setActiveSection(section);
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };
  const handleFilterChange = (status) => setFilter(status);
  const handleThemeChange = (newTheme) => setTheme(newTheme);

  const filteredRequests = requests.filter((req) => filter === "All" || req.status === filter);

  return (
    <div className={`dashboard-container ${theme.toLowerCase()}-theme`}>
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li className={activeSection === "Dashboard" ? "active" : ""} onClick={() => handleSidebarClick("Dashboard")}>
            Dashboard
          </li>
          <li className={activeSection === "Requests" ? "active" : ""} onClick={() => handleSidebarClick("Requests")}>
            Requests
          </li>
          <li className={activeSection === "Reports" ? "active" : ""} onClick={() => handleSidebarClick("Reports")}>
            Reports
          </li>
          <li className={activeSection === "Settings" ? "active" : ""} onClick={() => handleSidebarClick("Settings")}>
            Settings
          </li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        {activeSection === "Dashboard" && (
          <>
            <h1>Dashboard</h1>
            <div className="dashboard-analytics">
              <div className="analytics-card total">
                <h3>Total Requests</h3>
                <p>{requests.length}</p>
              </div>
              <div className="analytics-card pending">
                <h3>Pending Requests</h3>
                <p>{requests.filter((req) => req.status === "Pending").length}</p>
              </div>
              <div className="analytics-card resolved">
                <h3>Resolved Requests</h3>
                <p>{requests.filter((req) => req.status === "Resolved").length}</p>
              </div>
            </div>
          </>
        )}

        {activeSection === "Requests" && (
          <>
            <h1>Requests</h1>
            <div className="filter-section">
              <input
                type="text"
                placeholder="Enter a new request"
                value={newRequest}
                onChange={(e) => setNewRequest(e.target.value)}
                className="input-field"
              />
              <button onClick={handleAddRequest} className="primary-button">Add Request</button>
              <select onChange={(e) => handleFilterChange(e.target.value)} className="filter-dropdown">
                <option value="All">All</option>
                <option value="Pending">Pending</option>
                <option value="Resolved">Resolved</option>
                <option value="In Progress">In Progress</option>
              </select>
            </div>
            <div className="requests-table">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRequests.map((req) => (
                    <tr key={req.id}>
                      <td>{req.title}</td>
                      <td className={`status ${req.status.toLowerCase()}`}>{req.status}</td>
                      <td>{req.date}</td>
                      <td>
                        {req.status === "Pending" && (
                          <button onClick={() => handleResolveRequest(req.id)} className="resolve-button">Resolve</button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {activeSection === "Reports" && (
          <>
            <h1>Reports</h1>
            <div className="timeline">
              {requests.filter((req) => req.status === "Resolved").map((req) => (
                <div key={req.id} className="timeline-item">
                  <h3>{req.title}</h3>
                  <p>Resolved on: {req.date}</p>
                  <p>Description: {req.description}</p>
                </div>
              ))}
            </div>
          </>
        )}

        {activeSection === "Settings" && (
          <>
            <h1>Settings</h1>
            <div className="settings-content">
              <p>Manage your account settings, themes, and preferences here.</p>
              <h3>Change Theme</h3>
              <div className="theme-selector">
                <button onClick={() => handleThemeChange("Light")} className={`theme-button ${theme === "Light" ? "active" : ""}`}>Light Theme</button>
                <button onClick={() => handleThemeChange("Dark")} className={`theme-button ${theme === "Dark" ? "active" : ""}`}>Dark Theme</button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
