import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import logo from "../assets/logo.png";

const Dashboard = () => {
  const navigate = useNavigate();
  const role = localStorage.getItem("role"); // Fetch role from localStorage
  const [requests, setRequests] = useState([
    { id: 1, title: "System Upgrade", status: "Pending", date: "2023-12-01", description: "" },
    { id: 2, title: "Printer Issue", status: "Resolved", date: "2023-12-02", description: "Replaced faulty hardware." },
    { id: 3, title: "Network Downtime", status: "In Progress", date: "2023-12-03", description: "" },
    { id: 4, title: "Software Installation", status: "Pending", date: "2023-12-04", description: "" },
  ]);
  const [newRequest, setNewRequest] = useState("");

  const handleLogout = () => {
    localStorage.removeItem("role");
    localStorage.removeItem("token");
    navigate("/");
  };

  const handleAddRequest = () => {
    if (newRequest.trim() !== "") {
      setRequests([
        ...requests,
        { id: Date.now(), title: newRequest, status: "Pending", date: new Date().toISOString().split("T")[0], description: "" },
      ]);
      setNewRequest("");
    }
  };

  const handleResolveRequest = (id) => {
    const description = prompt("Please enter a resolution description:");
    if (description) {
      const updatedRequests = requests.map((req) =>
        req.id === id ? { ...req, status: "Resolved", date: new Date().toISOString().split("T")[0], description } : req
      );
      setRequests(updatedRequests);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <div className="logo-container">
          <img src={logo} alt="Service Portal Logo" className="logo" />
        </div>
        <ul>
          <li>Dashboard</li>
          {role === "admin" && <li>Requests</li>}
          <li>Reports</li>
          <li>Settings</li>
        </ul>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="main-content">
        <h1>Welcome, {role === "admin" ? "Admin" : "User"}!</h1>
        {role === "admin" ? (
          <>
            <h2>Requests</h2>
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Status</th>
                  <th>Date</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {requests.map((req) => (
                  <tr key={req.id}>
                    <td>{req.title}</td>
                    <td>{req.status}</td>
                    <td>{req.date}</td>
                    <td>
                      {req.status === "Pending" && (
                        <button onClick={() => handleResolveRequest(req.id)}>Resolve</button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        ) : (
          <>
            <h2>Raise a New Request</h2>
            <input
              type="text"
              placeholder="Enter a new request"
              value={newRequest}
              onChange={(e) => setNewRequest(e.target.value)}
            />
            <button onClick={handleAddRequest}>Submit</button>
          </>
        )}
        <h2>Reports</h2>
        <ul>
          {requests
            .filter((req) => req.status === "Resolved")
            .map((req) => (
              <li key={req.id}>
                {req.title} - {req.description} (Resolved on {req.date})
              </li>
            ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
